import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Code2, Braces, Palette, FileCode } from 'lucide-react';
import './nav.css';

function Nav() {
  const [menuOpen, setMenuOpen] = useState(false);

  const topics = [
    { name: 'React', icon: Code2, route: '/react-info' },
    { name: 'JavaScript', icon: Braces, route: '/javascript-info' },
    { name: 'CSS', icon: Palette, route: '/css-info' },
    { name: 'HTML', icon: FileCode, route: '/html' }
  ];

  return (
    <nav className="nav-container">
      <div className="nav-logo">MyApp</div>

      <button
        className="toggle-btn"
        onClick={() => setMenuOpen(!menuOpen)}
      >
        â˜°
      </button>

      <ul className={`nav-menu ${menuOpen ? 'open' : ''}`}>
        {topics.map((topic, index) => {
          const Icon = topic.icon;
          return (
            <li key={index}>
              <Link to={topic.route} className="nav-item" onClick={() => setMenuOpen(false)}>
                <Icon size={18} />
                <span>{topic.name}</span>
              </Link>
            </li>
          );
        })}
      </ul>
    </nav>
  );
}

export default Nav;
