import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  Code2, Braces, Palette, FileCode, Server, FileType, GitBranch, Globe, Clock,
  Database, Wrench, Zap, TestTube, Shield, Rocket, Link2, Bug, Star, Layers, Binary
} from 'lucide-react';
import './nav.css';

// Function to render the list of topics
const renderTopics = (topics, pathname) => {
  return topics.map((topic, index) => {
    const Icon = topic.icon;
    const isActive = pathname === topic.route;
    
    return (
      <li key={index}>
        {/* Use Link component with the route */}
        <Link 
          to={topic.route} 
          // Add 'active' class if the current path matches the topic route
          className={`nav-item ${isActive ? 'active' : ''}`}
        >
          <Icon size={18} />
          <span>{topic.name}</span>
        </Link>
      </li>
    );
  });
};

function Nav() {
  const location = useLocation(); // Hook to get the current URL path

  const topics = [
    { name: 'React', icon: Code2, route: '/react-info' },
    { name: 'JavaScript', icon: Braces, route: '/javascript-info' },
    { name: 'CSS', icon: Palette, route: '/css-Info' },
    { name: 'HTML', icon: FileCode, route: '/html' },
    { name: 'Node.js', icon: Server, route: '/nodejs-info' },
    { name: 'TypeScript', icon: FileType, route: '/typescript' },
    { name: 'Git&GitHub', icon: GitBranch, route: '/git' },
    { name: 'WebAPIs', icon: Globe, route: '/webapi' },
    { name: 'Testing', icon: TestTube, route: '/testing' },
    { name: 'Security', icon: Shield, route: '/security' },
    { name: 'Deployment', icon: Rocket, route: '/deployment' },
    { name: 'REST APIs', icon: Link2, route: '/restapi' },
    { name: 'Data Structures', icon: Binary, route: '/data-structures' },
    { name: 'Algorithms', icon: Binary, route: '/algorithms' },
    { name: 'Databases', icon: Database, route: '/databases' }
  ];

  return (
    <div className="nav-container">
      {/* For a clean UI, the logo should be outside the scrolling menu on mobile.
        I've added a logo wrapper here, even if it's empty, to show where it should be.
      */}
      <div className="nav-logo">Dev Topics</div>

      {/* CRITICAL FOR MARQUEE: We wrap the nav-menu in a separate div (nav-marquee)
        to manage the overflow: hidden and the animation start point on mobile. 
      */}
      <div className="nav-marquee">
        {/* CRITICAL FOR MARQUEE: Duplicate the content inside the ul 
          to ensure a seamless loop when the animation runs. 
          The keys must be unique, so we adjust them for the second set.
        */}
        <ul className="nav-menu">
          {/* First Set */}
          {renderTopics(topics, location.pathname)}
          {/* Second Set (Duplicated for Marquee Loop) */}
          {topics.map((topic, index) => {
             const Icon = topic.icon;
             const isActive = location.pathname === topic.route;
             return (
               <li key={`duplicate-${index}`}>
                 <Link 
                   to={topic.route} 
                   className={`nav-item ${isActive ? 'active' : ''}`}
                 >
                   <Icon size={18} />
                   <span>{topic.name}</span>
                 </Link>
               </li>
             );
          })}
        </ul>
      </div>
    </div>
  );
}

export default Nav;
