import React, { useState } from 'react';
import { Menu, X, Home, Info, Briefcase, Mail } from 'lucide-react';

const navItems = [
  { name: 'Home', href: '#home', icon: Home },
  { name: 'About', href: '#about', icon: Info },
  { name: 'Services', href: '#services', icon: Briefcase },
  { name: 'Contact', href: '#contact', icon: Mail },
];

function Navigation() {
  // State to manage the visibility of the mobile menu
  const [isOpen, setIsOpen] = useState(false);

  // Function to toggle the menu state
  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <>
      {/* CRITICAL FIX: Embedding CSS using <style> to resolve the 
        "Could not resolve './Navigation.css'" error and ensure compilation.
      */}
      <style>{`
        /* Navigation Styles
           Using standard CSS for a responsive, mobile-first navbar layout.
        */

        :root {
          --primary-color: #3b82f6; /* Blue-500 */
          --secondary-color: #60a5fa; /* Blue-400 */
          --text-color: #f3f4f6; /* Gray-100 */
          --bg-color: #1f2937; /* Gray-800 */
        }

        /* Ensure global box-sizing is correct */
        * {
          box-sizing: border-box;
          font-family: Arial, sans-serif;
        }

        /* --------------------
           1. Base Navigation Bar 
           -------------------- */

        .nav-bar {
          background-color: var(--bg-color);
          color: var(--text-color);
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          display: flex;
          flex-direction: column; 
          padding: 0.5rem 1rem; 
        }

        /* Header (Logo + Button) - Always visible */
        .nav-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          width: 100%;
        }

        .nav-logo {
          font-size: 1.5rem;
          font-weight: 700;
          color: var(--primary-color);
          text-decoration: none;
          padding: 0.5rem 0;
        }

        /* --------------------
           2. Menu Button (Mobile Only)
           -------------------- */

        .menu-button {
          background: none;
          border: 1px solid var(--secondary-color);
          border-radius: 0.375rem; 
          padding: 0.5rem;
          cursor: pointer;
          color: var(--text-color);
          display: block; 
          transition: all 0.2s ease;
        }

        .menu-button:hover {
          background-color: rgba(96, 165, 250, 0.1); 
        }

        /* --------------------
           3. Menu Items (Mobile View)
           -------------------- */

        .nav-menu {
          list-style: none;
          padding: 0;
          margin: 0;
          
          /* Mobile: Hidden by default (collapsed state) */
          display: none;
          flex-direction: column;
          width: 100%;
          margin-top: 0.5rem;
          transition: max-height 0.3s ease-in-out;
          max-height: 0;
          overflow: hidden;
        }

        .nav-menu.is-open {
          /* Mobile: Visible when the menu button is clicked */
          display: flex;
          max-height: 500px; /* Large enough value to show content */
        }

        .nav-item {
          width: 100%;
          border-top: 1px solid rgba(243, 244, 246, 0.1); 
        }

        .nav-link {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          padding: 0.75rem 0; 
          text-decoration: none;
          color: var(--text-color);
          transition: color 0.2s ease, background-color 0.2s ease, padding 0.2s ease;
        }
        
        .nav-icon {
          color: var(--secondary-color);
        }

        .nav-link:hover {
          color: var(--primary-color);
          background-color: rgba(255, 255, 255, 0.05);
          padding-left: 0.5rem; 
        }

        .nav-link:hover .nav-icon {
          color: var(--primary-color);
        }

        /* --------------------
           4. Desktop View (Media Query)
           -------------------- */

        @media (min-width: 768px) {
          .nav-bar {
            /* Desktop: Use flex-row to align logo and menu horizontally */
            flex-direction: row;
            justify-content: space-between;
            align-items: center;
            padding: 0.75rem 2rem;
          }
          
          /* Hide the hamburger button on desktop */
          .menu-button {
            display: none;
          }

          /* Desktop Menu: Show menu items in a horizontal row */
          .nav-menu {
            display: flex;
            flex-direction: row;
            align-items: center;
            gap: 1.5rem; 
            width: auto;
            margin-top: 0;
            max-height: none; 
            overflow: visible;
          }
          
          .nav-item {
            width: auto;
            border-top: none; 
          }

          .nav-link {
            padding: 0.5rem 0;
          }

          .nav-link:hover {
            padding-left: 0; 
            color: var(--primary-color);
            background-color: transparent;
            border-bottom: 2px solid var(--primary-color); 
          }
        }
      `}</style>

      <nav className="nav-bar">
        {/* Container for Logo and Menu Button */}
        <div className="nav-header">
          <a href="#top" className="nav-logo">
            MySite
          </a>
          
          {/* Mobile Menu Button */}
          <button 
            onClick={toggleMenu} 
            className="menu-button" 
            aria-expanded={isOpen}
            aria-controls="nav-menu-items"
          >
            {isOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Navigation Menu (Desktop & Mobile) */}
        <ul 
          id="nav-menu-items" 
          className={`nav-menu ${isOpen ? 'is-open' : ''}`}
        >
          {navItems.map((item) => {
            const Icon = item.icon;
            return (
              <li key={item.name} className="nav-item">
                <a 
                  href={item.href} 
                  className="nav-link"
                  onClick={() => setIsOpen(false)} // Close menu on link click (mobile)
                >
                  <Icon size={18} className="nav-icon" /> 
                  <span>{item.name}</span>
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
    </>
  );
}

export default Navigation;
