import React, { useState } from "react";
import "./n.css";

const menuItems = [
  { icon: "ðŸ ", text: "Home" },
  { icon: "â„¹ï¸", text: "About" },
  { icon: "ðŸ’»", text: "Web Development" },
  { icon: "ðŸ¤–", text: "AI Solutions" },
  { icon: "ðŸŽ¨", text: "UI/UX Design" },
  { icon: "ðŸ“ž", text: "Contact" },
  { icon: "ðŸ“š", text: "Blog" },
  { icon: "ðŸ› ï¸", text: "Tools" },
];

const Navigation = () => {
  const [showAll, setShowAll] = useState(false);

  const visibleItems = showAll ? menuItems : menuItems.slice(0, 4);

  return (
    <nav className="nav-container">
      <div className="nav-logo">MyCompany</div>
      <ul className="nav-menu">
        {visibleItems.map((item, index) => (
          <li key={index} className="nav-item">
            <span>{item.icon}</span>
            <span>{item.text}</span>
          </li>
        ))}

        {/* Show More / Show Less button */}
        {menuItems.length > 4 && (
          <li
            className="nav-item"
            style={{ fontWeight: "bold", color: "#10b981" }}
            onClick={() => setShowAll(!showAll)}
          >
            {showAll ? "Show Less" : "Show More"}
          </li>
        )}
      </ul>
    </nav>
  );
};

export default Navigation;
