import React, { useState } from 'react';
import topics from '../Data/NodeJS.json';
import './TechStack.css';
import { FaNodeJs } from 'react-icons/fa';

function NodeJSInfo() {
  const [selectedTopic, setSelectedTopic] = useState(topics);
  
  return (
    <div className="main-content">
      <h2><FaNodeJs color="#3C873A" /> {selectedTopic.title}</h2>
      <div className="content-section">
        <h3>Overview</h3>
        <p style={{ whiteSpace: 'pre-line' }}>{selectedTopic.content}</p>
      </div>
      <div className="content-section">
        <h3>Practice Questions</h3>
        <ol>
          {selectedTopic.questions.map((q, i) => (
            <li key={i}>{q}</li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default NodeJSInfo;
