import React, { useState } from 'react';
import { Code } from 'lucide-react';

function ReactHooks() {
  const [completed, setCompleted] = useState(false);

  const toggleComplete = () => {
    setCompleted(!completed);
  };

  // Static data for React Hooks (all major hooks explained)
  const hooksTopic = {
    title: 'React Hooks',
    content: `React Hooks are functions that let you "hook into" React state and lifecycle features from function components. They allow you to use state and other React features without writing class components. Hooks make your code cleaner, reusable, and easier to maintain.`,
    qa_pairs: [
      {
        question: '1. useState',
        answer: `**Definition:** useState lets you add state to functional components.

**Example:**
\`\`\`javascript
const [count, setCount] = useState(0);
<button onClick={() => setCount(count + 1)}>Click {count}</button>
\`\`\`

**Explanation:** Calling setCount updates state and triggers a re-render.`
      },
      {
        question: '2. useEffect',
        answer: `**Definition:** useEffect lets you perform side effects in functional components, like fetching data or subscribing to events.

**Example:**
\`\`\`javascript
useEffect(() => {
  document.title = \`Clicked \${count} times\`;
}, [count]);
\`\`\`

**Explanation:** Runs after every render; dependencies array controls when it runs.`
      },
      {
        question: '3. useContext',
        answer: `**Definition:** useContext allows you to consume context in functional components.

**Example:**
\`\`\`javascript
const theme = useContext(ThemeContext);
\`\`\`

**Explanation:** Useful for sharing global data without props drilling.`
      },
      {
        question: '4. useReducer',
        answer: `**Definition:** useReducer is an alternative to useState for complex state logic.

**Example:**
\`\`\`javascript
const [state, dispatch] = useReducer(reducer, initialState);
dispatch({ type: 'increment' });
\`\`\`

**Explanation:** Handles state updates via a reducer function; ideal for multiple related state values.`
      },
      {
        question: '5. useRef',
        answer: `**Definition:** useRef returns a mutable object to persist values across renders without triggering a re-render.

**Example:**
\`\`\`javascript
const inputRef = useRef();
inputRef.current.focus();
\`\`\`

**Explanation:** Useful for DOM access, storing previous values, or timers.`
      },
      {
        question: '6. useMemo',
        answer: `**Definition:** useMemo memoizes expensive computations.

**Example:**
\`\`\`javascript
const expensiveValue = useMemo(() => computeExpensiveValue(a, b), [a, b]);
\`\`\`

**Explanation:** Only recalculates when dependencies change, improving performance.`
      },
      {
        question: '7. useCallback',
        answer: `**Definition:** useCallback returns a memoized callback function.

**Example:**
\`\`\`javascript
const handleClick = useCallback(() => {
  console.log(count);
}, [count]);
\`\`\`

**Explanation:** Prevents unnecessary function re-creations, useful in optimized child components.`
      },
      {
        question: '8. Custom Hooks',
        answer: `**Definition:** Custom hooks let you extract and reuse logic across components.

**Example:**
\`\`\`javascript
function useCounter(initialValue) {
  const [count, setCount] = useState(initialValue);
  const increment = () => setCount(count + 1);
  return { count, increment };
}
\`\`\`

**Explanation:** Helps keep code DRY and organized.`
      }
    ]
  };

  const renderContent = (text) => {
    // Split by code blocks
    const parts = text.split(/```(\w+)?\n([\s\S]*?)```/g);
    
    return parts.map((part, idx) => {
      // Every 3rd element starting at index 2 is code content
      if ((idx - 2) % 3 === 0) {
        return (
          <pre key={idx} className="bg-gray-800 text-green-400 p-3 rounded-md overflow-x-auto text-sm my-2">
            <code>{part}</code>
          </pre>
        );
      }
      // Regular text with bold support
      const textParts = part.split(/\*\*(.*?)\*\*/g);
      return (
        <span key={idx}>
          {textParts.map((textPart, i) => 
            i % 2 === 1 ? <strong key={i} className="font-semibold">{textPart}</strong> : textPart
          )}
        </span>
      );
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Code className="w-8 h-8 text-blue-600" />
              <h1 className="text-3xl font-bold text-gray-800">{hooksTopic.title}</h1>
            </div>
            <button
              onClick={toggleComplete}
              className={`px-6 py-2 rounded-lg font-medium transition-colors ${
                completed
                  ? 'bg-green-500 text-white hover:bg-green-600'
                  : 'bg-blue-500 text-white hover:bg-blue-600'
              }`}
            >
              {completed ? '✓ Completed' : 'Mark Complete'}
            </button>
          </div>
        </div>

        {/* Overview */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Overview</h2>
          <p className="text-gray-700 leading-relaxed">{hooksTopic.content}</p>
        </div>

        {/* Hooks List */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">All Major React Hooks Explained</h2>
          <div className="space-y-6">
            {hooksTopic.qa_pairs.map((qa, index) => (
              <div key={index} className="border-l-4 border-blue-500 pl-4 py-2">
                <h3 className="text-xl font-semibold text-gray-800 mb-3">{qa.question}</h3>
                <div className="text-gray-700 leading-relaxed whitespace-pre-line">
                  {renderContent(qa.answer)}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ReactHooks;
