import React, { useState } from 'react';
import './ReactInfo.css';

function ReactInfo() {
  const topics = [
    {
      id: 1,
      title: 'Introduction',
      content: `React is a JavaScript library developed by Facebook for building fast and interactive user interfaces.
      It allows developers to create reusable UI components and manage the application state efficiently.`
    },
    {
      id: 2,
      title:  'Setting Up',
      content: `To start with React, you can use:
- Create React App: npx create-react-app my-app
- Vite: npm create vite@latest my-app
- CodeSandbox for online learning`
    },
    {
      id: 3,
      title: 'Components',
      content: `Components are the building blocks of React applications. There are two types:
1. Functional Components
2. Class Components

Functional Components are simpler and use hooks for state and lifecycle management.`
    },
    {
      id: 4,
      title: 'JSX',
      content: `JSX is a syntax extension that allows writing HTML-like code in JavaScript.
Example:
const element = <h1>Hello, React!</h1>;`
    },
    {
      id: 5,
      title: 'Props',
      content: `Props (short for properties) are used to pass data from parent components to child components.
Example:
<ChildComponent name="Shadulla" />`
    },
    {
      id: 6,
      title: 'State',
      content: `State is used to manage data inside a component that can change over time.
Example using useState hook:
const [count, setCount] = useState(0);`
    },
    {
      id: 7,
      title: 'Event Handling',
      content: `Events in React are handled using camelCase syntax.
Example:
<button onClick={handleClick}>Click Me</button>`
    },
    {
      id: 8,
      title: 'Conditional Rendering',
      content: `You can render elements conditionally using if statements or ternary operators.
Example:
{isLoggedIn ? <Logout /> : <Login />}`
    },
    {
      id: 9,
      title: 'Lists and Keys',
      content: `Use map() to render lists. Each element should have a unique 'key' prop.
Example:
{items.map(item => <li key={item.id}>{item.name}</li>)}`
    },
    {
      id: 10,
      title: 'Hooks',
      content: `Common hooks in React:
- useState: For state management
- useEffect: For side effects (fetching data, timers)
- useContext: For global state sharing
- useRef: To access DOM elements`
    },
    {
      id: 11,
      title: 'React Router',
      content: `React Router is used for navigation in React apps.
Example:
<BrowserRouter>
  <Routes>
    <Route path="/" element={<Home />} />
    <Route path="/about" element={<About />} />
  </Routes>
</BrowserRouter>`
    },
    {
      id: 12,
      title: 'Forms',
      content: `Forms are handled using controlled components in React.
Example:
<input type="text" value={name} onChange={e => setName(e.target.value)} />`
    },
    {
      id: 13,
      title: 'Lifecycle Methods',
      content: `For class components:
- componentDidMount
- componentDidUpdate
- componentWillUnmount

For functional components, use useEffect to mimic lifecycle behavior.`
    },
    {
      id: 14,
      title: 'Context API',
      content: `Context API allows passing data globally without prop drilling.
Example:
const MyContext = React.createContext();
<MyContext.Provider value={value}>`
    },
    {
      id: 15,
      title: 'Advanced Topics',
      content: `- Higher Order Components (HOC)
- Render Props
- Error Boundaries
- React.memo for optimization`
    }
  ];

  const [selectedTopic, setSelectedTopic] = useState(topics[0]);

  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>React Topics</h3>
        <ul>
          {topics.map(topic => (
            <li
              key={topic.id}
              className={selectedTopic.id === topic.id ? 'active' : ''}
              onClick={() => setSelectedTopic(topic)}
            >
              {topic.title}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h2>{selectedTopic.title}</h2>
        <p style={{ whiteSpace: 'pre-line' }}>{selectedTopic.content}</p>
      </div>
    </div>
  );
}

export default ReactInfo;
