import React, { useState } from 'react';
import { FaReact } from 'react-icons/fa';

function ReactHooks() {
  const [completed, setCompleted] = useState(false);

  const toggleComplete = () => {
    setCompleted(!completed);
  };

  // Static data for React Hooks (all major hooks explained)
  const hooksTopic = {
    title: 'React Hooks',
    content: `
React Hooks are functions that let you "hook into" React state and lifecycle features from function components. 
They allow you to use state and other React features without writing class components. 
Hooks make your code cleaner, reusable, and easier to maintain.
    `,
    qa_pairs: [
      {
        question: '1. useState',
        answer: `
**Definition:** useState lets you add state to functional components.  

**Example:**
\`\`\`javascript
const [count, setCount] = useState(0);
<button onClick={() => setCount(count + 1)}>Click {count}</button>
\`\`\`

**Explanation:** Calling setCount updates state and triggers a re-render.
        `
      },
      {
        question: '2. useEffect',
        answer: `
**Definition:** useEffect lets you perform side effects in functional components, like fetching data or subscribing to events.  

**Example:**
\`\`\`javascript
useEffect(() => {
  document.title = \`Clicked \${count} times\`;
}, [count]);
\`\`\`

**Explanation:** Runs after every render; dependencies array controls when it runs.
        `
      },
      {
        question: '3. useContext',
        answer: `
**Definition:** useContext allows you to consume context in functional components.  

**Example:**
\`\`\`javascript
const theme = useContext(ThemeContext);
\`\`\`

**Explanation:** Useful for sharing global data without props drilling.
        `
      },
      {
        question: '4. useReducer',
        answer: `
**Definition:** useReducer is an alternative to useState for complex state logic.  

**Example:**
\`\`\`javascript
const [state, dispatch] = useReducer(reducer, initialState);
dispatch({ type: 'increment' });
\`\`\`

**Explanation:** Handles state updates via a reducer function; ideal for multiple related state values.
        `
      },
      {
        question: '5. useRef',
        answer: `
**Definition:** useRef returns a mutable object to persist values across renders without triggering a re-render.  

**Example:**
\`\`\`javascript
const inputRef = useRef();
<input ref={inputRef} />
inputRef.current.focus();
\`\`\`

**Explanation:** Useful for DOM access, storing previous values, or timers.
        `
      },
      {
        question: '6. useMemo',
        answer: `
**Definition:** useMemo memoizes expensive computations.  

**Example:**
\`\`\`javascript
const expensiveValue = useMemo(() => computeExpensiveValue(a, b), [a, b]);
\`\`\`

**Explanation:** Only recalculates when dependencies change, improving performance.
        `
      },
      {
        question: '7. useCallback',
        answer: `
**Definition:** useCallback returns a memoized callback function.  

**Example:**
\`\`\`javascript
const handleClick = useCallback(() => { console.log(count); }, [count]);
\`\`\`

**Explanation:** Prevents unnecessary function re-creations, useful in optimized child components.
        `
      },
      {
        question: '8. Custom Hooks',
        answer: `
**Definition:** Custom hooks let you extract and reuse logic across components.  

**Example:**
\`\`\`javascript
function useCounter(initialValue) {
  const [count, setCount] = useState(initialValue);
  const increment = () => setCount(count + 1);
  return { count, increment };
}
\`\`\`

**Explanation:** Helps keep code DRY and organized.
        `
      }
    ]
  };

  // --- Inline Styles (Simulating CSS) ---

  const containerStyle = {
    maxWidth: '900px',
    margin: '30px auto',
    padding: '40px',
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    boxShadow: '0 10px 30px rgba(0, 0, 0, 0.08)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottom: '2px solid #667eea',
    paddingBottom: '20px',
    marginBottom: '30px',
  };

  const titleStyle = {
    display: 'flex',
    alignItems: 'center',
    gap: '15px',
    fontSize: '2.2em',
    color: '#1e293b',
    margin: '0',
  };

  const baseButtonStyle = {
    padding: '10px 20px',
    border: '2px solid #667eea',
    borderRadius: '8px',
    backgroundColor: '#ffffff',
    color: '#667eea',
    fontSize: '1em',
    fontWeight: '600',
    cursor: 'pointer',
    marginBottom: '20px',
    transition: 'background-color 0.3s, color 0.3s',
  };

  const completedButtonStyle = {
    ...baseButtonStyle,
    backgroundColor: '#059669',
    borderColor: '#059669',
    color: '#ffffff',
  };

  const h2Style = {
    fontSize: '1.6em',
    color: '#475569',
    borderLeft: '5px solid #667eea',
    paddingLeft: '10px',
    marginTop: '30px',
    marginBottom: '20px',
  };

  const questionItemStyle = {
    backgroundColor: '#f8fafc',
    border: '1px solid #e2e8f0',
    borderRadius: '8px',
    padding: '20px',
    marginBottom: '20px',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.05)',
  };
  
  const questionTextStyle = {
    whiteSpace: 'pre-line', 
    marginTop: '6px', 
    color: '#475569'
  };

  // Function to dynamically apply style for the button's completed state
  const buttonStyle = completed ? completedButtonStyle : baseButtonStyle;


  return (
    <div className="container" style={containerStyle}>
      <div className="content-header" style={headerStyle}>
        <h1 className="content-title" style={titleStyle}>
          <FaReact size={40} color="#667eea" /> {hooksTopic.title}
        </h1>
        <button 
          className={`complete-btn ${completed ? 'completed' : ''}`}
          onClick={toggleComplete}
          style={buttonStyle}
        >
          {completed ? '✓ Completed' : 'Mark Complete'}
        </button>
      </div>

      <div className="content-section">
        <h2 style={h2Style}>Overview</h2>
        <p style={{ whiteSpace: 'pre-line', fontSize: '1.1em', color: '#475569' }}>{hooksTopic.content}</p>
      </div>

      <div className="content-section">
        <h2 style={h2Style}>All Major React Hooks Explained</h2>
        <ol className="questions-list" style={{ padding: '0', listStyle: 'none' }}>
          {hooksTopic.qa_pairs.map((qa, index) => (
            <li key={index} className="question-item" style={questionItemStyle}>
              <strong style={{ display: 'block', fontSize: '1.3em', color: '#1e293b', marginBottom: '10px', borderBottom: '1px dashed #cbd5e1', paddingBottom: '5px' }}>
                {qa.question}
              </strong>
              {/* Note: In a real app, markdown in `qa.answer` (like **Definition:** and `code blocks`) would need a separate library (like marked.js) to render correctly outside of a pre-line environment. Here, we just use pre-line to preserve structure. */}
              <p style={questionTextStyle}>{qa.answer}</p>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default ReactHooks;
