import React, { useState } from 'react';
import topics from '../Data/NodeJS.json';
import './Global.css';
import { FaNodeJs } from 'react-icons/fa';

function NodeJSInfo() {
  const [selectedTopic, setSelectedTopic] = useState(topics);
  const [completedTopics, setCompletedTopics] = useState(new Set());

  const toggleComplete = () => {
    const newCompleted = new Set(completedTopics);
    if (newCompleted.has(selectedTopic.id)) {
      newCompleted.delete(selectedTopic.id);
    } else {
      newCompleted.add(selectedTopic.id);
    }
    setCompletedTopics(newCompleted);
  };

  return (
    <div className="main-content">
      <h2>
        <FaNodeJs color="#3C873A" /> {selectedTopic.title}
      </h2>

      <button 
        className={`complete-btn ${completedTopics.has(selectedTopic.id) ? 'completed' : ''}`} 
        onClick={toggleComplete}
      >
        {completedTopics.has(selectedTopic.id) ? '✓ Completed' : 'Mark Complete'}
      </button>

      <div className="content-section">
        <h3>Overview</h3>
        <p style={{ whiteSpace: 'pre-line' }}>{selectedTopic.content}</p>
      </div>

      <div className="content-section">
        <h3>Practice Questions</h3>
        <ol>
          {selectedTopic.questions.map((q, i) => (
            <li key={i}>{q}</li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default NodeJSInfo;
