function ReactHooksGuide() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedHook, setSelectedHook] = useState(null);
  const [completedHooks, setCompletedHooks] = useState({});
  const [filterCategory, setFilterCategory] = useState('All');
  const [filterDifficulty, setFilterDifficulty] = useState('All');

  // Load completed hooks from storage
  useEffect(() => {
    const loadProgress = async () => {
      try {
        const result = await window.storage.get('react-hooks-progress');
        if (result && result.value) {
          setCompletedHooks(JSON.parse(result.value));
        }
      } catch (error) {
        console.log('No saved progress found');
      }
    };
    loadProgress();
  }, []);

  // Save progress
  const toggleCompleted = async (hookName) => {
    const newCompleted = {
      ...completedHooks,
      [hookName]: !completedHooks[hookName]
    };
    setCompletedHooks(newCompleted);
    
    try {
      await window.storage.set('react-hooks-progress', JSON.stringify(newCompleted));
    } catch (error) {
      console.error('Failed to save progress:', error);
    }
  };

  // Filter hooks
  const filteredHooks = useMemo(() => {
    return hooksData.filter(hook => {
      const matchesSearch = hook.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          hook.purpose.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = filterCategory === 'All' || hook.category === filterCategory;
      const matchesDifficulty = filterDifficulty === 'All' || hook.difficulty === filterDifficulty;
      
      return matchesSearch && matchesCategory && matchesDifficulty;
    });
  }, [searchQuery, filterCategory, filterDifficulty]);

  // Calculate progress
  const progress = useMemo(() => {
    const completed = Object.values(completedHooks).filter(Boolean).length;
    return Math.round((completed / hooksData.length) * 100);
  }, [completedHooks]);

  const categories = ['All', 'Basic', 'Advanced', 'Performance', 'Concurrent'];
  const difficulties = ['All', 'Beginner', 'Intermediate', 'Advanced'];

  const displayedHook = selectedHook || filteredHooks[0];

  return (
    <>
      <style>{styles}</style>
      <div className="hooks-guide">
        <div className="header">
          <div className="header-content">
            <div className="header-top">
              <div className="logo-section">
                <div className="logo-icon">
                  <Code color="white" size={28} />
                </div>
                <div className="logo-text">
                  <h1>React Hooks Mastery</h1>
                  <p>Complete reference guide with examples</p>
                </div>
              </div>
              <div className="progress-section">
                <div className="progress-text">
                  <div className="progress-label">Progress</div>
                  <div className="progress-value">{progress}%</div>
                </div>
                <div className="progress-ring">
                  <svg viewBox="0 0 36 36">
                    <path
                      d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                      fill="none"
                      stroke="#e5e7eb"
                      strokeWidth="3"
                    />
                    <path
                      d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                      fill="none"
                      stroke="url(#gradient)"
                      strokeWidth="3"
                      strokeDasharray={`${progress}, 100`}
                      strokeLinecap="round"
                    />
                    <defs>
                      <linearGradient id="gradient" x1="0%" y1="0%" x2="100%" y2="0%">
                        <stop offset="0%" stopColor="#667eea" />
                        <stop offset="100%" stopColor="#764ba2" />
                      </linearGradient>
                    </defs>
                  </svg>
                </div>
              </div>
            </div>

            <div className="filters-section">
              <div className="search-wrapper">
                <Search className="search-icon" size={20} />
                <input
                  type="text"
                  placeholder="Search hooks..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
              </div>
              <select
                value={filterCategory}
                onChange={(e) => setFilterCategory(e.target.value)}
                className="filter-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat} Category</option>
                ))}
              </select>
              <select
                value={filterDifficulty}
                onChange={(e) => setFilterDifficulty(e.target.value)}
                className="filter-select"
              >
                {difficulties.map(diff => (
                  <option key={diff} value={diff}>{diff} Level</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        <div className="main-container">
          <div className="content-grid">
            <div className="sidebar">
              <div className="sidebar-header">
                <BookOpen size={20} style={{ color: '#667eea' }} />
                <h3 className="sidebar-title">All Hooks ({filteredHooks.length})</h3>
              </div>
              <ul className="hooks-list">
                {filteredHooks.map((hook) => (
                  <li
                    key={hook.name}
                    onClick={() => setSelectedHook(hook)}
                    className={`hook-item ${displayedHook?.name === hook.name ? 'active' : ''}`}
                  >
                    <div className="hook-item-left">
                      {completedHooks[hook.name] ? (
                        <CheckCircle2 size={18} color="#10b981" />
                      ) : (
                        <Circle size={18} color="#d1d5db" />
                      )}
                      <span className="hook-item-name">{hook.name}</span>
                    </div>
                    <span className={`difficulty-badge difficulty-${hook.difficulty.toLowerCase()}`}>
                      {hook.difficulty[0]}
                    </span>
                  </li>
                ))}
              </ul>
            </div>
            <div className="main-content">
              {displayedHook && (
                <>
                  <div className="content-header">
                    <div className="header-flex">
                      <div className="hook-title-section">
                        <div className="hook-badges">
                          <h2 className="hook-name">{displayedHook.name}</h2>
                          <span className="badge badge-difficulty">
                            {displayedHook.difficulty}
                          </span>
                          <span className="badge badge-category">
                            {displayedHook.category}
                          </span>
                        </div>
                        <p className="hook-purpose">{displayedHook.purpose}</p>
                      </div>
                      <button
                        onClick={() => toggleCompleted(displayedHook.name)}
                        className={`complete-button ${completedHooks[displayedHook.name] ? 'completed' : ''}`}
                      >
                        {completedHooks[displayedHook.name] ? (
                          <>
                            <CheckCircle2 size={20} />
                            Completed
                          </>
                        ) : (
                          <>
                            <Circle size={20} />
                            Mark Complete
                          </>
                        )}
                      </button>
                    </div>
                  </div>

                  <div className="content-body">
                    <section className="section">
                      <h3 className="section-title">
                        <Code size={22} style={{ color: '#667eea' }} />
                        Syntax
                      </h3>
                      <div className="code-block">
                        {displayedHook.syntax}
                      </div>
                    </section>

                    <section className="section">
                      <h3 className="section-title">
                        <Zap size={22} style={{ color: '#667eea' }} />
                        Example Usage
                      </h3>
                      <div className="code-block">
                        <pre>{displayedHook.example}</pre>
                      </div>
                    </section>

                    <section className="section">
                      <h3 className="section-title">
                        <BookOpen size={22} style={{ color: '#667eea' }} />
                        Common Use Cases
                      </h3>
                      <div className="use-cases-grid">
                        {displayedHook.useCases.map((useCase, i) => (
                          <div key={i} className="use-case-card">
                            <div className="use-case-number">{i + 1}</div>
                            <p className="use-case-text">{useCase}</p>
                          </div>
                        ))}
                      </div>
                    </section>

                    <section className="section">
                      <h3 className="section-title">
                        <Lightbulb size={22} style={{ color: '#10b981' }} />
                        Pro Tips
                      </h3>
                      <div className="tips-list">
                        {displayedHook.tips.map((tip, i) => (
                          <div key={i} className="tip-item">
                            <Lightbulb size={18} style={{ color: '#10b981' }} />
                            <p className="tip-text">{tip}</p>
                          </div>
                        ))}
                      </div>
                    </section>

                    <section className="section">
                      <h3 className="section-title">
                        <AlertTriangle size={22} style={{ color: '#ef4444' }} />
                        Common Mistakes to Avoid
                      </h3>
                      <div className="mistakes-list">
                        {displayedHook.commonMistakes.map((mistake, i) => (
                          <div key={i} className="mistake-item">
                            <AlertTriangle size={18} style={{ color: '#ef4444' }} />
                            <p className="mistake-text">{mistake}</p>
                          </div>
                        ))}
                      </div>
                    </section>

                    {displayedHook.relatedHooks.length > 0 && (
                      <section className="section">
                        <h3 className="section-title">Related Hooks</h3>
                        <div className="related-hooks">
                          {displayedHook.relatedHooks.map((related) => {
                            const relatedHook = hooksData.find(h => h.name === related);
                            return (
                              <button
                                key={related}
                                onClick={() => setSelectedHook(relatedHook)}
                                className="related-hook-button"
                              >
                                {related}
                              </button>
                            );
                          })}
                        </div>
                      </section>
                    )}

                    <div className="navigation">
                      <button
                        onClick={() => {
                          const currentIndex = filteredHooks.findIndex(h => h.name === displayedHook.name);
                          if (currentIndex > 0) {
                            setSelectedHook(filteredHooks[currentIndex - 1]);
                            window.scrollTo({ top: 0, behavior: 'smooth' });
                          }
                        }}
                        disabled={filteredHooks.findIndex(h => h.name === displayedHook.name) === 0}
                        className="nav-button prev"
                      >
                        â† Previous
                      </button>
                      <button
                        onClick={() => {
                          const currentIndex = filteredHooks.findIndex(h => h.name === displayedHook.name);
                          if (currentIndex < filteredHooks.length - 1) {
                            setSelectedHook(filteredHooks[currentIndex + 1]);
                            window.scrollTo({ top: 0, behavior: 'smooth' });
                          }
                        }}
                        disabled={filteredHooks.findIndex(h => h.name === displayedHook.name) === filteredHooks.length - 1}
                        className="nav-button next"
                      >
                        Next â†’
                      </button>
                    </div>
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ReactHooksGuide;
