import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function ReactInfo() {
  const topics = [
    { id: 1, title: 'Introduction', content: 'React is a JS library for building user interfaces.' },
    { id: 2, title: 'Components', content: 'Components are reusable building blocks of a React app.' },
    { id: 3, title: 'JSX', content: 'JSX lets you write HTML-like code in React.' },
    { id: 4, title: 'State', content: 'State is used to store data inside a component that can change over time.' },
    { id: 5, title: 'Props', content: 'Props are used to pass data from parent to child components.' },
  ];

  const [selectedTopic, setSelectedTopic] = useState(topics[0]);

  return (
    <div className="container-fluid">
      <div className="row vh-100">
        {/* Sidebar */}
        <div className="col-3 bg-light border-end p-3">
          <h4>React Topics</h4>
          <div className="list-group">
            {topics.map(topic => (
              <button
                key={topic.id}
                className={`list-group-item list-group-item-action ${selectedTopic.id === topic.id ? 'active' : ''}`}
                onClick={() => setSelectedTopic(topic)}
              >
                {topic.title}
              </button>
            ))}
          </div>
        </div>

        {/* Main Content */}
        <div className="col-9 p-4">
          <h2>{selectedTopic.title}</h2>
          <p>{selectedTopic.content}</p>
        </div>
      </div>
    </div>
  );
}

export default ReactInfo;
