import React, { useState, useEffect, useRef, useContext, useReducer, useMemo, useCallback } from "react";

// --- Custom Hook Example ---
const useDocumentTitle = (title) => {
  useEffect(() => {
    document.title = title;
  }, [title]);
};
// ----------------------------

// --- useContext setup ---
// 1. Create a Context
const ThemeContext = React.createContext('light');

// 2. Component that consumes the Context
const ThemeDisplay = () => {
  const theme = useContext(ThemeContext);
  return <p style={{ color: theme === 'dark' ? '#DCDCDC' : '#2D2D2D', marginTop: '10px' }}>Current Theme: **{theme}**</p>;
};
// ------------------------

// --- useReducer setup ---
const initialCounterState = { count: 0 };

const counterReducer = (state, action) => {
  switch (action.type) {
    case 'increment':
      return { count: state.count + 1 };
    case 'decrement':
      return { count: state.count - 1 };
    case 'reset':
      return { count: 0 };
    default:
      return state;
  }
};
// ------------------------


export default function ReactHookDemo() {
  // Apply the dark theme styles
  const styles = {
    container: {
      fontFamily: "Arial, sans-serif",
      padding: "20px",
      maxWidth: "100vw",
      margin: "0 auto",
      lineHeight: "1.6",
      // Dark theme colors
      backgroundColor: "#000000ff", // Very dark background
      color: "#DCDCDC", // Light grey text
      minHeight: "100vh",
    },
    heading: {
      fontSize: "36px",
      fontWeight: "bold",
      marginBottom: "20px",
      color: "#BB86FC", // Bright, accent color
      textAlign: "center",
      borderBottom: "2px solid #000000ff",
      paddingBottom: "10px",
    },
    section: {
      marginBottom: "30px",
      padding: "40px",
      display: "grid",
      
      borderRadius: "12px",
      background: "#1B1B1B", // Slightly lighter dark background
      boxShadow: "0 4px 10px rgba(0,0,0,0.5)",
    },
    sectionHeading: {
      color: "#BB86FC",
      fontSize: "24px",
      marginBottom: "15px",
    },
    codeBox: {
      background: "#000000", // Pure black for code blocks
      color: "#C3F8FF", // Light blue/cyan for code text
      padding: "15px",
      borderRadius: "8px",
      marginTop: "10px",
      whiteSpace: "pre-wrap",
      fontSize: "14px",
      overflowX: "auto",
      border: "1px solid #373737",
    },
    button: {
      padding: "10px 15px",
      margin: "5px",
      borderRadius: "6px",
      cursor: "pointer",
      fontWeight: "bold",
      backgroundColor: "#03DAC6", // Secondary accent color for buttons
      color: "#000000",
      border: "none",
      transition: "background-color 0.2s",
    },
    input: {
      padding: "10px",
      marginRight: "10px",
      borderRadius: "6px",
      border: "1px solid #373737",
      background: "#333333",
      color: "#DCDCDC",
    }
  };

  // --- 1. useState Example ---
  const [count, setCount] = useState(0);
  const [theme, setTheme] = useState('dark');

  // --- 2. useEffect Example ---
  // Side effect 1: Update document title
  useDocumentTitle(`Count: ${count}`);

  // Side effect 2: Log on mount and unmount (simulated)
  useEffect(() => {
    console.log("Component Mounted or Count/Theme Changed. Count:", count, "Theme:", theme);
    // Cleanup function (runs on unmount or before next effect)
    return () => {
      console.log("Cleanup: Effect is being torn down.");
    };
  }, [count, theme]); // Reruns when count or theme changes

  // --- 3. useRef Example ---
  const inputRef = useRef(null);
  const mutableValueRef = useRef(0); // For storing a value without triggering re-render

  const focusInput = () => {
    inputRef.current.focus();
    // Doesn't trigger re-render:
    mutableValueRef.current += 1;
    console.log("Mutable Value Ref:", mutableValueRef.current);
  };

  // --- 4. useContext Example ---
  // ThemeProvider is implicitly wrapped around the ThemeDisplay in the return

  // --- 5. useReducer Example ---
  const [state, dispatch] = useReducer(counterReducer, initialCounterState);

  // --- 6. useMemo Example ---
  // Simulates an expensive calculation
  const expensiveCalculation = (num) => {
    console.log("Running expensive calculation...");
    for (let i = 0; i < 10000000; i++) {
      num += 1;
    }
    return num;
  };
  // Only re-runs when 'count' changes, not on every render
  const memoizedValue = useMemo(() => expensiveCalculation(count), [count]);

  // --- 7. useCallback Example ---
  // Prevents the 'incrementCount' function from being recreated on every render
  const incrementCount = useCallback(() => {
    setCount(c => c + 1);
  }, []); // Empty dependency array: function will be the same across all renders

  return (
    // We use the ThemeContext.Provider here to make 'theme' available to its children
    <ThemeContext.Provider value={theme}>
      <div style={styles.container}>
        <h1 style={styles.heading}>‚öõÔ∏è React Hooks Deep Dive (Dark Mode)</h1>
        
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>What are React Hooks?</h2>
          <p>
            React Hooks are **functions** that let you "hook into" React state and lifecycle features from functional components. They were introduced in React 16.8 to allow developers to build entire UIs using only functional components, eliminating the need for class components.
          </p>
          <ul>
            <li>**The Rules:** Only call Hooks at the top level of a functional component or a custom Hook. Don't call them inside loops, conditions, or nested functions.</li>
          </ul>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>1. useState üé£ (State Management)</h2>
          <p>
            The fundamental Hook for managing **local component state**. It returns a pair: the **current state value** and a **function** to update it.
          </p>
          <button style={styles.button} onClick={incrementCount}>
            Increase Count ({count})
          </button>
          <button style={styles.button} onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}>
            Toggle Theme ({theme})
          </button>
          <div style={styles.codeBox}>{`
// Syntax: [currentState, setStateFunction] = useState(initialValue);
const [count, setCount] = useState(0);

// Using the memoized callback from the example below
const incrementCount = useCallback(() => {
  setCount(c => c + 1); // Functional update is best practice
}, []);

<button onClick={incrementCount}>Increase Count ({count})</button>
`}</div>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>2. useEffect üöÄ (Side Effects)</h2>
          <p>
            Used to manage side effects, which are operations that happen outside of the normal React rendering cycle (e.g., API calls, subscriptions, manual DOM changes, timers).
          </p>
          <p>
            **Example Side Effect:** The document title for this page is currently set to: **{document.title}**
          </p>
          <p>
            The effect runs **after** every render by default. You control when it runs using the **dependency array** (the second argument).
          </p>
          <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '15px' }}>
            <thead>
              <tr style={{ background: '#373737' }}>
                <th style={{ padding: '8px', border: '1px solid #373737', textAlign: 'left' }}>Dependency Array</th>
                <th style={{ padding: '8px', border: '1px solid #373737', textAlign: 'left' }}>When Effect Runs</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>`[count, theme]`</td>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>Only when **count** or **theme** changes.</td>
              </tr>
              <tr>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>`[]` (Empty Array)</td>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>Only once, after the **initial render** (like `componentDidMount`).</td>
              </tr>
              <tr>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>*Omitted*</td>
                <td style={{ padding: '8px', border: '1px solid #373737' }}>After **every single render** (rarely used).</td>
              </tr>
            </tbody>
          </table>
          <div style={styles.codeBox}>{`
// Custom Hook that uses useEffect to set the document title
const useDocumentTitle = (title) => {
  useEffect(() => {
    document.title = title;
    // Optional cleanup function
    return () => {
      // Clean up subscriptions, listeners, or timers here
    };
  }, [title]); // Only re-run if 'title' changes
};
`}</div>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>3. useRef üìå (Mutable Values / DOM Access)</h2>
          <p>
            Used to get a reference to a **DOM element** or to hold a **mutable value** that persists across renders but **doesn't cause a re-render** when it changes.
          </p>
          <input style={styles.input} ref={inputRef} placeholder="Click button to focus" />
          <button style={styles.button} onClick={focusInput}>
            Focus Input
          </button>
          <p style={{ marginTop: '10px' }}>
            *The mutable value ref is: {mutableValueRef.current} (Check console for updates on focus)*
          </p>
          <div style={styles.codeBox}>{`
const inputRef = useRef(null);
const mutableValueRef = useRef(0); // Initial value

const focusInput = () => {
  inputRef.current.focus(); // Accessing the DOM element
  mutableValueRef.current += 1; // Mutating a value without re-render
};

<input ref={inputRef} />
<button onClick={focusInput}>Focus Input</button>
`}</div>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>4. useContext üåê (Global State/Theming)</h2>
          <p>
            Allows a component to subscribe to React context without introducing nesting. It solves **prop drilling** (passing props manually through many levels).
          </p>
          <ThemeDisplay />
          <div style={styles.codeBox}>{`
// 1. Create the Context (usually in a separate file)
const ThemeContext = React.createContext('light');

// 2. Wrap a provider around the components that need the data
<ThemeContext.Provider value={theme}>...</ThemeContext.Provider>

// 3. Consume the value in any descendant component
const theme = useContext(ThemeContext);
`}</div>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>5. useReducer üß† (Complex State Logic)</h2>
          <p>
            An alternative to `useState` for managing more **complex state logic** that involves multiple sub-values or when the next state depends on the previous one. It's often paired with `useContext` for global state management.
          </p>
          <p>Current Count: **{state.count}**</p>
          <button style={styles.button} onClick={() => dispatch({ type: 'increment' })}>Increment</button>
          <button style={styles.button} onClick={() => dispatch({ type: 'decrement' })}>Decrement</button>
          <button style={styles.button} onClick={() => dispatch({ type: 'reset' })}>Reset</button>
          <div style={styles.codeBox}>{`
// Define the reducer function
const counterReducer = (state, action) => {
  switch (action.type) {
    case 'increment': return { count: state.count + 1 };
    // ... other cases
    default: return state;
  }
};

// Use it in the component
const [state, dispatch] = useReducer(counterReducer, { count: 0 });

// Trigger an update
<button onClick={() => dispatch({ type: 'increment' })}>Increment</button>
`}</div>
        </div>

    

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>6. useMemo & 7. useCallback ‚ö° (Performance Optimization)</h2>
          <p>
            These are **memoization** Hooks used to optimize performance by caching expensive computations (`useMemo`) or functions (`useCallback`) so they are not recreated or re-run on every render.
          </p>
          <p>
            **useMemo:** Memoized value (Expensive calculation): **{memoizedValue}**
          </p>
          <p>
            **useCallback:** The `incrementCount` function used for `useState` is now memoized.
          </p>
          <div style={styles.codeBox}>{`
// useMemo: Only re-runs the expensive function when a dependency changes
const memoizedValue = useMemo(() => expensiveCalculation(count), [count]);

// useCallback: Returns a memoized version of a callback function
const incrementCount = useCallback(() => {
  setCount(c => c + 1);
}, []); // Function reference only changes if dependencies change
`}</div>
        </div>

        

        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>Custom Hooks üõ†Ô∏è</h2>
          <p>
            Custom Hooks are functions (named starting with `use`) that call other Hooks and allow you to **reuse stateful logic** across different components easily.
          </p>
          <p>
            **Example:** The `useDocumentTitle` Hook in this component is a custom Hook that abstracts the logic for changing the document title based on the state.
          </p>
          <div style={styles.codeBox}>{`
// customHook.js
const useDocumentTitle = (title) => {
  useEffect(() => {
    document.title = title;
  }, [title]);
};

// MyComponent.js
useDocumentTitle(\`User: \${userName}\`);
`}</div>
        </div>
      </div>
    </ThemeContext.Provider>
  );
}
