import React, { useState } from 'react';
import './Global.css';
import { FaReact } from 'react-icons/fa';

function ReactHooks() {
  const [completed, setCompleted] = useState(false);

  const toggleComplete = () => {
    setCompleted(!completed);
  };

  // Static data for React Hooks
  const hooksTopic = {
    id: 1,
    title: 'React Hooks',
    content: `
Hooks are functions that let you “hook into” React state and lifecycle features from function components.
Common hooks include useState, useEffect, useContext, useReducer, useRef, and custom hooks.
They allow you to write cleaner, reusable code without using class components.
    `,
    qa_pairs: [
      {
        question: 'What is the purpose of useState in React?',
        answer: 'useState is a hook that allows you to add state to functional components.'
      },
      {
        question: 'When should you use useEffect?',
        answer: 'useEffect is used to perform side effects in functional components, like fetching data or subscribing to events.'
      },
      {
        question: 'Can you create custom hooks?',
        answer: 'Yes, custom hooks let you extract and reuse logic between components.'
      }
    ]
  };

  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar open">
        <div className="sidebar-header">
          <h3 className='nav-title'>React Hooks</h3>
        </div>
        <ul>
          <li className={`active ${completed ? 'completed' : ''}`}>
            <span className="topic-number">{hooksTopic.id}</span>
            <span className="topic-title">
              <FaReact size={24} color="#667eea" />
              {hooksTopic.title}
            </span>
            {completed && <span className="check-mark">✓</span>}
          </li>
        </ul>
      </div>

      {/* Main content */}
      <div className="main-content">
        <div className="content-header">
          <h2 className="content-title">
            <FaReact size={40} color="#667eea" />
            <span className="topic-badge">{hooksTopic.id}.</span>
            {hooksTopic.title}
          </h2>
          <button 
            className={`complete-btn ${completed ? 'completed' : ''}`}
            onClick={toggleComplete}
          >
            {completed ? '✓ Completed' : 'Mark Complete'}
          </button>
        </div>

        <div className="content-section">
          <h3>Overview</h3>
          <p style={{ whiteSpace: 'pre-line' }}>{hooksTopic.content}</p>
        </div>

        <div className="content-section">
          <h3>Practice Questions</h3>
          <ol className="questions-list">
            {hooksTopic.qa_pairs.map((qa, index) => (
              <li key={index} className="question-item">
                <strong>Q:</strong> {qa.question} <br />
                <strong>A:</strong> {qa.answer}
              </li>
            ))}
          </ol>
        </div>
      </div>
    </div>
  );
}

export default ReactHooks;
