import React, { useState } from 'react';
import './ReactInfo.css';

function ReactInfo() {
  const topics = [
    { id: 1, title: 'Introduction', content:{} },
    { id: 2, title: 'Components', content: 'Components are reusable building blocks of a React app.' },
    { id: 3, title: 'JSX', content: 'JSX lets you write HTML-like code in React.' },
    { id: 4, title: 'State', content: 'State stores data inside a component that can change over time.' },
    { id: 5, title: 'Props', content: 'Props are used to pass data from parent to child components.' },
  ];

  const [selectedTopic, setSelectedTopic] = useState(topics[0]);

  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar">
        <h3>React Topics</h3>
        <ul>
          {topics.map(topic => (
            <li
              key={topic.id}
              className={selectedTopic.id === topic.id ? 'active' : ''}
              onClick={() => setSelectedTopic(topic)}
            >
              {topic.title}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h2>{selectedTopic.title}</h2>
        <p>{selectedTopic.content}</p>
      </div>
    </div>
  );
}

export default ReactInfo;
