import React, { useState } from 'react';
import topics from '../Data/reactTopics.json';
import './ReactInfo.css';

function ReactInfo() {
  const [selectedTopic, setSelectedTopic] = useState(topics[0]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  return (
    <div className="container">
      <a className="toggle-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
        {sidebarOpen ? '<' : '>'}
      </a>

      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <h3>React Topics</h3>
        <ul>
          {topics.map(topic => (
            <li
              key={topic.id}
              className={selectedTopic.id === topic.id ? 'active' : ''}
              onClick={() => setSelectedTopic(topic)}
            >
              {topic.title}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-grow-1 p-4">
        <button
          className="btn btn-primary mb-3"
          onClick={() => setSidebarOpen(!sidebarOpen)}
        >
          {sidebarOpen ? 'Hide Sidebar' : 'Show Sidebar'}
        </button>

        <h2>{selectedTopic.title}</h2>
        <p>{selectedTopic.content}</p>

        {/* Questions Accordion */}
        <div className="accordion" id="questionsAccordion">
          {selectedTopic.questions.map((q, index) => (
            <div className="accordion-item" key={index}>
              <h2 className="accordion-header" id={`heading${index}`}>
                <button
                  className="accordion-button collapsed"
                  type="button"
                  data-bs-toggle="collapse"
                  data-bs-target={`#collapse${index}`}
                  aria-expanded="false"
                  aria-controls={`collapse${index}`}
                >
                  Question {index + 1}
                </button>
              </h2>
              <div
                id={`collapse${index}`}
                className="accordion-collapse collapse"
                aria-labelledby={`heading${index}`}
                data-bs-parent="#questionsAccordion"
              >
                <div className="accordion-body">{q}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ReactInfo;
