import React, { useState, useEffect, useRef, useContext, useReducer, useMemo, useCallback } from "react";

// Custom Hook Example
const useDocumentTitle = (title) => {
  useEffect(() => {
    document.title = title;
  }, [title]);
};

// useContext setup
const ThemeContext = React.createContext('light');
const ThemeDisplay = () => {
  const theme = useContext(ThemeContext);
  return (
    <p style={{ color: theme === 'dark' ? '#DCDCDC' : '#2D2D2D', marginTop: '10px' }}>
      Current Theme: <strong>{theme}</strong>
    </p>
  );
};

// useReducer setup
const initialCounterState = { count: 0 };
const counterReducer = (state, action) => {
  switch (action.type) {
    case 'increment': return { count: state.count + 1 };
    case 'decrement': return { count: state.count - 1 };
    case 'reset': return { count: 0 };
    default: return state;
  }
};

export default function ReactHookDemo() {
  // --- Styling ---
  const styles = {
    container: {
    display: "grid",
    gridTemplateColumns: "repeat(3, 1fr)", // 3 equal columns
    gridGap: "20px", // space between items
      fontFamily: "Arial, sans-serif",
      padding: "20px",
      backgroundColor: "#000000ff",
      color: "#E0E0E0",
      minHeight: "100vh",
      lineHeight: "1.6",
    },
    heading: {
      fontSize: "36px",
      fontWeight: "bold",
      color: "#BB86FC",
      textAlign: "center",
      marginBottom: "30px",
    },
    section: {
      marginBottom: "25px",
      padding: "30px",
      borderRadius: "10px",
      backgroundColor: "#101010ff",
    
    },
    sectionHeading: {
      fontSize: "24px",
      color: "#BB86FC",
      marginBottom: "15px",
    },
    codeBox: {
      backgroundColor: "#0D0D0D",
      color: "#C3F8FF",
      padding: "15px",
      borderRadius: "8px",
      fontSize: "14px",
      marginTop: "10px",
      overflowX: "auto",
      border: "1px solid #373737",
    },
    button: {
      padding: "10px 15px",
      margin: "5px",
      borderRadius: "6px",
      cursor: "pointer",
      fontWeight: "bold",
      backgroundColor: "#03DAC6",
      color: "#000000",
      border: "none",
    },
    input: {
      padding: "10px",
      marginRight: "10px",
      borderRadius: "6px",
      border: "1px solid #373737",
      background: "#333333",
      color: "#E0E0E0",
    },
  };

  // --- React Hook Examples ---
  const [count, setCount] = useState(0);
  const [theme, setTheme] = useState('dark');
  useDocumentTitle(`Count: ${count}`);

  const inputRef = useRef(null);
  const mutableValueRef = useRef(0);
  const focusInput = () => {
    inputRef.current.focus();
    mutableValueRef.current += 1;
    console.log("Mutable Ref:", mutableValueRef.current);
  };

  const [state, dispatch] = useReducer(counterReducer, initialCounterState);

  const expensiveCalculation = (num) => {
    for (let i = 0; i < 10000000; i++) num += 1;
    return num;
  };
  const memoizedValue = useMemo(() => expensiveCalculation(count), [count]);

  const incrementCount = useCallback(() => setCount(c => c + 1), []);

  return (
    <ThemeContext.Provider value={theme}>
      <div style={styles.container}>

        {/* What are Hooks */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>Introduction to React Hooks</h2>
          <p>
            React Hooks are functions that allow functional components to use **state** and **lifecycle features**.  
            They help eliminate class components and make code simpler and reusable.
          </p>
          <ul>
            <li>Follow <strong>Rules of Hooks:</strong> Call only at top-level of component or custom hook, not inside loops or conditions.</li>
          </ul>
        </div>

        {/* useState */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>1. useState üé£</h2>
          <p>Manages **local state** in functional components.</p>
          <button style={styles.button} onClick={incrementCount}>
            Increase Count ({count})
          </button>
          <button style={styles.button} onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}>
            Toggle Theme ({theme})
          </button>
          <div style={styles.codeBox}>{`const [count, setCount] = useState(0);\nconst incrementCount = useCallback(() => setCount(c => c + 1), []);`}</div>
        </div>

        {/* useEffect */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>2. useEffect üöÄ</h2>
          <p>Manages side-effects like API calls, subscriptions, or DOM changes.</p>
          <p><strong>Document title:</strong> {document.title}</p>
          <div style={styles.codeBox}>{`useEffect(() => { document.title = 'Count: ' + count; }, [count]);`}</div>
        </div>

        {/* useRef */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>3. useRef üìå</h2>
          <p>Access DOM elements or store **mutable values** without triggering re-render.</p>
          <input style={styles.input} ref={inputRef} placeholder="Click button to focus" />
          <button style={styles.button} onClick={focusInput}>Focus Input</button>
          <p>Mutable ref value: {mutableValueRef.current} (Check console on click)</p>
          <div style={styles.codeBox}>{`const inputRef = useRef(null);\nconst mutableValueRef = useRef(0);`}</div>
        </div>

        {/* useContext */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>4. useContext üåê</h2>
          <p>Provides global state to avoid prop drilling.</p>
          <ThemeDisplay />
          <div style={styles.codeBox}>{`const theme = useContext(ThemeContext);\n<ThemeContext.Provider value={theme}>...</ThemeContext.Provider>`}</div>
        </div>

        {/* useReducer */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>5. useReducer üß†</h2>
          <p>Manages complex state logic.</p>
          <p>Current Count: {state.count}</p>
          <button style={styles.button} onClick={() => dispatch({ type: 'increment' })}>Increment</button>
          <button style={styles.button} onClick={() => dispatch({ type: 'decrement' })}>Decrement</button>
          <button style={styles.button} onClick={() => dispatch({ type: 'reset' })}>Reset</button>
          <div style={styles.codeBox}>{`const [state, dispatch] = useReducer(counterReducer, { count: 0 });`}</div>
        </div>

        {/* useMemo & useCallback */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>6. useMemo & 7. useCallback ‚ö°</h2>
          <p>Optimize performance:</p>
          <ul>
            <li><strong>useMemo:</strong> Memoizes expensive values ‚Üí {memoizedValue}</li>
            <li><strong>useCallback:</strong> Memoizes functions to avoid re-creation ‚Üí incrementCount()</li>
          </ul>
          <div style={styles.codeBox}>{`const memoizedValue = useMemo(() => expensiveCalculation(count), [count]);\nconst incrementCount = useCallback(() => setCount(c => c + 1), []);`}</div>
        </div>

        {/* Custom Hooks */}
        <div style={styles.section}>
          <h2 style={styles.sectionHeading}>Custom Hooks üõ†Ô∏è</h2>
          <p>Reusable stateful logic. Example: <code>useDocumentTitle</code>.</p>
          <div style={styles.codeBox}>{`const useDocumentTitle = (title) => { useEffect(() => { document.title = title; }, [title]); };`}</div>
        </div>
      </div>
    </ThemeContext.Provider>
  );
