import React, { useState, useEffect, useMemo } from 'react';
import { Search, CheckCircle2, Circle, BookOpen, Code, Lightbulb, AlertTriangle, Zap, Filter } from 'lucide-react';

const hooksData = [
  {
    name: "useState",
    category: "Basic",
    purpose: "Manages state in functional components. Returns a state value and a function to update it.",
    syntax: "const [state, setState] = useState(initialValue);",
    example: `const [count, setCount] = useState(0);
const [user, setUser] = useState({ name: '', age: 0 });

// Updating state
setCount(count + 1);
setCount(prev => prev + 1); // Functional update

// Updating object state
setUser({ ...user, name: 'John' });`,
    useCases: [
      "Form inputs and controlled components",
      "Toggle states (modals, dropdowns)",
      "Counters and numeric values",
      "Simple component-level data"
    ],
    tips: [
      "Use functional updates when new state depends on previous state",
      "Initialize with function for expensive computations: useState(() => expensiveCalc())",
      "Don't mutate state directly - always create new references",
      "Multiple useState calls are fine for unrelated state"
    ],
    commonMistakes: [
      "Mutating state directly: user.name = 'John' âŒ",
      "Not using functional updates in loops or callbacks",
      "Storing derived data in state instead of computing it",
      "Using one giant state object when separate states are clearer"
    ],
    relatedHooks: ["useReducer", "useRef"],
    difficulty: "Beginner"
  },
  {
    name: "useEffect",
    category: "Basic",
    purpose: "Performs side effects in functional components. Runs after render and can clean up.",
    syntax: "useEffect(() => { /* effect */ return () => { /* cleanup */ }; }, [dependencies]);",
    example: `// Run once on mount
useEffect(() => {
  fetchData();
}, []);

// Run when dependencies change
useEffect(() => {
  document.title = \`Count: \${count}\`;
}, [count]);

// Cleanup function
useEffect(() => {
  const timer = setInterval(() => tick(), 1000);
  return () => clearInterval(timer);
}, []);`,
    useCases: [
      "Data fetching from APIs",
      "Subscribing to events or WebSockets",
      "Setting up timers or intervals",
      "Updating document title or meta tags",
      "Integrating with third-party libraries"
    ],
    tips: [
      "Always specify dependencies array to avoid infinite loops",
      "Use empty [] for mount-only effects",
      "Return cleanup function for subscriptions and timers",
      "Separate concerns into multiple useEffect calls",
      "Consider useLayoutEffect for DOM measurements"
    ],
    commonMistakes: [
      "Missing dependencies causing stale closures",
      "Not cleaning up subscriptions/timers",
      "Fetching data without proper cleanup or cancellation",
      "Putting too much logic in a single useEffect",
      "Forgetting that effects run after render, not before"
    ],
    relatedHooks: ["useLayoutEffect", "useCallback", "useMemo"],
    difficulty: "Beginner"
  },
  {
    name: "useContext",
    category: "Basic",
    purpose: "Consumes context values without wrapping components in Context.Consumer.",
    syntax: "const value = useContext(MyContext);",
    example: `const ThemeContext = React.createContext('light');

function ThemedButton() {
  const theme = useContext(ThemeContext);
  return (
    <button className={theme}>
      Click me
    </button>
  );
}

// In parent component
<ThemeContext.Provider value="dark">
  <ThemedButton />
</ThemeContext.Provider>`,
    useCases: [
      "Theme management (dark/light mode)",
      "User authentication state",
      "Language/locale preferences",
      "Global app settings",
      "Avoiding prop drilling"
    ],
    tips: [
      "Create custom hooks: const useTheme = () => useContext(ThemeContext)",
      "Memoize context values to prevent unnecessary re-renders",
      "Split contexts by concern (AuthContext, ThemeContext, etc.)",
      "Use with useMemo for expensive context values",
      "Consider useReducer for complex context state"
    ],
    commonMistakes: [
      "Not wrapping with Provider - causes default value usage",
      "Creating new context value objects on every render",
      "Using context for all state (overusing it)",
      "Not memoizing expensive context values",
      "Forgetting that all consumers re-render on context change"
    ],
    relatedHooks: ["useReducer", "useMemo"],
    difficulty: "Beginner"
  },
  {
    name: "useReducer",
    category: "Advanced",
    purpose: "Alternative to useState for complex state logic. Uses reducer pattern like Redux.",
    syntax: "const [state, dispatch] = useReducer(reducer, initialState);",
    example: `const initialState = { count: 0, history: [] };

function reducer(state, action) {
  switch (action.type) {
    case 'increment':
      return { 
        count: state.count + 1,
        history: [...state.history, 'increment']
      };
    case 'decrement':
      return { 
        count: state.count - 1,
        history: [...state.history, 'decrement']
      };
    case 'reset':
      return initialState;
    default:
      throw new Error();
  }
}

const [state, dispatch] = useReducer(reducer, initialState);
dispatch({ type: 'increment' });`,
    useCases: [
      "Complex state with multiple sub-values",
      "State transitions with specific logic",
      "Form state management",
      "Undo/redo functionality",
      "State machines"
    ],
    tips: [
      "Use when next state depends on previous state",
      "Better than useState for complex updates",
      "Combine with useContext for global state management",
      "Actions can include payload data",
      "Reducer must be pure function"
    ],
    commonMistakes: [
      "Mutating state in reducer instead of returning new state",
