function ReactHooks() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedHook, setSelectedHook] = useState(null);
  const [completedHooks, setCompletedHooks] = useState({});
  const [filterCategory, setFilterCategory] = useState('All');
  const [filterDifficulty, setFilterDifficulty] = useState('All');

  // Load completed hooks from storage
  useEffect(() => {
    const loadProgress = async () => {
      try {
        const result = await window.storage.get('react-hooks-progress');
        if (result && result.value) {
          setCompletedHooks(JSON.parse(result.value));
        }
      } catch (error) {
        console.log('No saved progress found');
      }
    };
    loadProgress();
  }, []);

  // Save progress
  const toggleCompleted = async (hookName) => {
    const newCompleted = {
      ...completedHooks,
      [hookName]: !completedHooks[hookName]
    };
    setCompletedHooks(newCompleted);
    
    try {
      await window.storage.set('react-hooks-progress', JSON.stringify(newCompleted));
    } catch (error) {
      console.error('Failed to save progress:', error);
    }
  };

  // Filter hooks
  const filteredHooks = useMemo(() => {
    return hooksData.filter(hook => {
      const matchesSearch = hook.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          hook.purpose.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesCategory = filterCategory === 'All' || hook.category === filterCategory;
      const matchesDifficulty = filterDifficulty === 'All' || hook.difficulty === filterDifficulty;
      
      return matchesSearch && matchesCategory && matchesDifficulty;
    });
  }, [searchQuery, filterCategory, filterDifficulty]);

  // Calculate progress
  const progress = useMemo(() => {
    const completed = Object.values(completedHooks).filter(Boolean).length;
    return Math.round((completed / hooksData.length) * 100);
  }, [completedHooks]);

  const categories = ['All', 'Basic', 'Advanced', 'Performance', 'Concurrent'];
  const difficulties = ['All', 'Beginner', 'Intermediate', 'Advanced'];

  const displayedHook = selectedHook || filteredHooks[0];

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 sticky top-0 z-10 shadow-sm">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-br from-indigo-600 to-purple-600 rounded-lg flex items-center justify-center">
                <Code className="text-white" size={24} />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">React Hooks Mastery</h1>
                <p className="text-sm text-gray-600">Complete reference guide with examples</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-right">
                <div className="text-sm text-gray-600">Progress</div>
                <div className="text-2xl font-bold text-indigo-600">{progress}%</div>
              </div>
              <div className="w-16 h-16">
                <svg className="transform -rotate-90" viewBox="0 0 36 36">
                  <path
                    d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                    fill="none"
                    stroke="#e5e7eb"
                    strokeWidth="3"
                  />
                  <path
                    d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                    fill="none"
                    stroke="#6366f1"
                    strokeWidth="3"
                    strokeDasharray={`${progress}, 100`}
                    strokeLinecap="round"
                  />
                </svg>
              </div>
            </div>
          </div>

          {/* Search and Filters */}
          <div className="flex gap-3 flex-wrap">
            <div className="flex-1 min-w-[300px] relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search hooks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              />
            </div>
            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat} Category</option>
              ))}
            </select>
            <select
              value={filterDifficulty}
              onChange={(e) => setFilterDifficulty(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
            >
              {difficulties.map(diff => (
                <option key={diff} value={diff}>{diff} Level</option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-4 sticky top-32">
              <h3 className="font-semibold text-gray-900 mb-3 flex items-center gap-2">
                <BookOpen size={18} className="text-indigo-600" />
                All Hooks ({filteredHooks.length})
              </h3>
              <div className="space-y-1 max-h-[calc(100vh-250px)] overflow-y-auto">
                {filteredHooks.map((hook) => (
                  <button
                    key={hook.name}
                    onClick={() => setSelectedHook(hook)}
                    className={`w-full text-left px-3 py-2 rounded-lg transition-all flex items-center justify-between group ${
                      displayedHook?.name === hook.name
                        ? 'bg-indigo-50 text-indigo-700 font-medium'
                        : 'hover:bg-gray-50 text-gray-700'
                    }`}
                  >
                    <span className="flex items-center gap-2">
                      {completedHooks[hook.name] ? (
                        <CheckCircle2 size={16} className="text-green-500 flex-shrink-0" />
                      ) : (
                        <Circle size={16} className="text-gray-300 flex-shrink-0" />
                      )}
                      <span className="truncate">{hook.name}</span>
                    </span>
                    <span className={`text-xs px-2 py-0.5 rounded ${
                      hook.difficulty === 'Beginner' ? 'bg-green-100 text-green-700' :
                      hook.difficulty === 'Intermediate' ? 'bg-yellow-100 text-yellow-700' :
                      'bg-red-100 text-red-700'
                    }`}>
                      {hook.difficulty[0]}
                    </span>
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            {displayedHook && (
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
                {/* Header */}
                <div className="bg-gradient-to-r from-indigo-600 to-purple-600 p-6 text-white">
                  <div className="flex items-start justify-between">
                    <div>
                      <div className="flex items-center gap-3 mb-2">
                        <h2 className="text-3xl font-bold">{displayedHook.name}</h2>
                        <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                          displayedHook.difficulty === 'Beginner' ? 'bg-green-500' :
                          displayedHook.difficulty === 'Intermediate' ? 'bg-yellow-500' :
                          'bg-red-500'
                        }`}>
                          {displayedHook.difficulty}
                        </span>
                        <span className="px-3 py-1 rounded-full text-sm font-medium bg-white/20">
                          {displayedHook.category}
                        </span>
                      </div>
                      <p className="text-indigo-100 text-lg">{displayedHook.purpose}</p>
                    </div>
                    <button
                      onClick={() => toggleCompleted(displayedHook.name)}
                      className={`px-4 py-2 rounded-lg font-medium transition-all flex items-center gap-2 ${
                        completedHooks[displayedHook.name]
                          ? 'bg-green-500 text-white'
                          : 'bg-white text-indigo-600 hover:bg-indigo-50'
                      }`}
                    >
                      {completedHooks[displayedHook.name] ? (
                        <>
                          <CheckCircle2 size={18} />
                          Completed
                        </>
                      ) : (
                        <>
                          <Circle size={18} />
                          Mark Complete
                        </>
                      )}
                    </button>
                  </div>
                </div>

                <div className="p-6 space-y-6">
                  {/* Syntax */}
                  <section>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center gap-2">
                      <Code size={20} className="text-indigo-600" />
                      Syntax
                    </h3>
                    <div className="bg-gray-900 text-gray-100 p-4 rounded-lg font-mono text-sm overflow-x-auto">
                      {displayedHook.syntax}
                    </div>
                  </section>

                  {/* Example */}
                  <section>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center gap-2">
                      <Zap size={20} className="text-indigo-600" />
                      Example Usage
                    </h3>
                    <div className="bg-gray-900 text-gray-100 p-4 rounded-lg overflow-x-auto">
                      <pre className="font-mono text-sm whitespace-pre-wrap">{displayedHook.example}</pre>
                    </div>
                  </section>

                  {/* Use Cases */}
                  <section>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center gap-2">
                      <BookOpen size={20} className="text-indigo-600" />
                      Common Use Cases
                    </h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                      {displayedHook.useCases.map((useCase, i) => (
                        <div key={i} className="flex items-start gap-3 p-3 bg-indigo-50 rounded-lg">
                          <div className="w-6 h-6 bg-indigo-600 text-white rounded-full flex items-center justify-center text-xs font-bold flex-shrink-0 mt-0.5">
                            {i + 1}
                          </div>
                          <p className="text-gray-700 text-sm">{useCase}</p>
                        </div>
                      ))}
                    </div>
                  </section>

                  {/* Tips */}
                  <section>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center gap-2">
                      <Lightbulb size={20} className="text-green-600" />
                      Pro Tips
                    </h3>
                    <div className="space-y-2">
                      {displayedHook.tips.map((tip, i) => (
                        <div key={i} className="flex items-start gap-3 p-3 bg-green-50 border-l-4 border-green-500 rounded">
                          <Lightbulb size={16} className="text-green-600 flex-shrink-0 mt-0.5" />
                          <p className="text-gray-700 text-sm">{tip}</p>
                        </div>
                      ))}
                    </div>
                  </section>

                  {/* Common Mistakes */}
                  <section>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3 flex items-center gap-2">
                      <AlertTriangle size={20} className="text-red-600" />
                      Common Mistakes to Avoid
                    </h3>
                    <div className="space-y-2">
                      {displayedHook.commonMistakes.map((mistake, i) => (
                        <div key={i} className="flex items-start gap-3 p-3 bg-red-50 border-l-4 border-red-500 rounded">
                          <AlertTriangle size={16} className="text-red-600 flex-shrink-0 mt-0.5" />
                          <p className="text-gray-700 text-sm">{mistake}</p>
                        </div>
                      ))}
                    </div>
                  </section>

                  {/* Related Hooks */}
                  {displayedHook.relatedHooks.length > 0 && (
                    <section>
                      <h3 className="text-lg font-semibold text-gray-900 mb-3">Related Hooks</h3>
                      <div className="flex flex-wrap gap-2">
                        {displayedHook.relatedHooks.map((related) => {
                          const relatedHook = hooksData.find(h => h.name === related);
                          return (
                            <button
                              key={related}
                              onClick={() => setSelectedHook(relatedHook)}
                              className="px-4 py-2 bg-purple-100 text-purple-700 rounded-lg hover:bg-purple-200 transition-colors font-medium text-sm"
                            >
                              {related}
                            </button>
                          );
                        })}
                      </div>
                    </section>
                  )}

                  {/* Navigation */}
                  <div className="flex justify-between pt-6 border-t border-gray-200">
                    <button
                      onClick={() => {
                        const currentIndex = filteredHooks.findIndex(h => h.name === displayedHook.name);
                        if (currentIndex > 0) {
                          setSelectedHook(filteredHooks[currentIndex - 1]);
                          window.scrollTo({ top: 0, behavior: 'smooth' });
                        }
                      }}
                      disabled={filteredHooks.findIndex(h => h.name === displayedHook.name) === 0}
                      className="px-6 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      â† Previous
                    </button>
                    <button
                      onClick={() => {
                        const currentIndex = filteredHooks.findIndex(h => h.name === displayedHook.name);
                        if (currentIndex < filteredHooks.length - 1) {
                          setSelectedHook(filteredHooks[currentIndex + 1]);
                          window.scrollTo({ top: 0, behavior: 'smooth' });
                        }
                      }}
                      disabled={filteredHooks.findIndex(h => h.name === displayedHook.name) === filteredHooks.length - 1}
                      className="px-6 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors font-medium disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      Next â†’
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ReactHooks;
