import React, { useState } from 'react';
import './ReactInfo.css';

function ReactInfo() {
  const topics = [
    { id: 1, title: 'Introduction', content: 'React is a JS library for building user interfaces.' },
    { id: 2, title: 'Components', content: 'Components are reusable building blocks of a React app.' },
    { id: 3, title: 'JSX', content: 'JSX lets you write HTML-like code in React.' },
    { id: 4, title: 'State', content: 'State stores data inside a component that can change over time.' },
    { id: 5, title: 'Props', content: 'Props are used to pass data from parent to child components.' },
    { id: 6, title: 'Hooks', content: 'Hooks like useState, useEffect let you manage state & lifecycle in functional components.' },
    { id: 7, title: 'Routing', content: 'React Router is used for navigation between pages.' },
    { id: 8, title: 'Context API', content: 'Context API allows global state without prop drilling.' },
    { id: 9, title: 'Forms', content: 'Forms in React use controlled components.' },
    { id: 10, title: 'Advanced Topics', content: 'HOC, Render Props, Error Boundaries, React.memo for optimization.' }
  ];

  const [selectedTopic, setSelectedTopic] = useState(topics[0]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  return (
    <div className="container">
      {/* Toggle button */}
      <a className="toggle-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
        {sidebarOpen ? 'Close Sidebar' : 'Open Sidebar'}
      </a>

      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <br/> 
        <h3>React Topics</h3>
        <ul>
          {topics.map(topic => (
            <li
              key={topic.id}
              className={selectedTopic.id === topic.id ? 'active' : ''}
              onClick={() => setSelectedTopic(topic)}
            >
              {topic.title}
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <h2>{selectedTopic.title}</h2>
        <p style={{ whiteSpace: 'pre-line' }}>{selectedTopic.content}</p>
      </div>
    </div>
  );
}

export default ReactInfo;
