import React, { useState } from 'react';
import topics from '../Data/reactTopics.json';
import './ReactInfo.css';

function ReactInfo() {
  const [selectedTopic, setSelectedTopic] = useState(topics[0]);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  return (
    <div className="container">
      {/* Toggle Button */}
      <button className="toggle-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
        {sidebarOpen ? '‹' : '›'}
      </button>

      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <h3>React Topics</h3>
        <ul>
          {topics.map(topic => (
            <li
              key={topic.id}
              className={selectedTopic.id === topic.id ? 'active' : ''}
              onClick={() => setSelectedTopic(topic)}
            >
              <span className="topic-number">{topic.id}</span>
              <span className="topic-title">{topic.title}</span>
            </li>
          ))}
        </ul>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="content-header">
          <h2>{selectedTopic.title}</h2>
        </div>

        <div className="content-section">
          <p>{selectedTopic.content}</p>
          <ul className="questions-list">
            {selectedTopic.questions.map((q, index) => (
              <li key={index} className="question-item">{q}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default ReactInfo;
