import React, { useState, useEffect } from 'react';
import './Global.css';
import { FaReact } from 'react-icons/fa';
import hooksData from '../Data/reactHooks.json';

function ReactHooks() {
  const [hooks, setHooks] = useState([]);
  const [completedHooks, setCompletedHooks] = useState({});

  useEffect(() => {
    if (hooksData && hooksData.reactHooks) {
      setHooks(hooksData.reactHooks);
    }
  }, []);

  if (!hooks.length) return <div>Loading...</div>;

  const toggleCompleted = (index) => {
    setCompletedHooks((prev) => ({
      ...prev,
      [index]: !prev[index],
    }));
  };

  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar open">
        <div className="sidebar-header">
          <h3 className='nav-title'>React Hooks</h3>
        </div>
        <ul>
          {hooks.map((hook, index) => (
            <li
              key={index}
              className={completedHooks[index] ? 'completed' : ''}
              onClick={() => toggleCompleted(index)}
            >
              <span className="topic-number">{index + 1}</span>
              <span className="topic-title">
                <FaReact size={20} color="#667eea" /> {hook.name}
              </span>
              {completedHooks[index] && <span className="check-mark">✓</span>}
            </li>
          ))}
        </ul>
      </div>

      {/* Main content */}
      <div className="main-content">
        <h2 className="content-title">
          <FaReact size={40} color="#667eea" /> React Hooks Overview
        </h2>

        {hooks.map((hook, index) => (
          <div key={index} className="content-section" style={{ marginBottom: '24px' }}>
            <h3>
              {index + 1}. {hook.name}{' '}
              <button
                className={`complete-btn ${completedHooks[index] ? 'completed' : ''}`}
                onClick={() => toggleCompleted(index)}
              >
                {completedHooks[index] ? '✓ Completed' : 'Mark Complete'}
              </button>
            </h3>
            <p><strong>Purpose:</strong> {hook.purpose}</p>
            <p><strong>Example:</strong></p>
            <pre style={{ background: '#f5f5f5', padding: '10px' }}>{hook.example}</pre>
            <p><strong>Tips:</strong></p>
            <ul>
              {hook.tips.map((tip, i) => <li key={i}>{tip}</li>)}
            </ul>
            <p><strong>Common Mistakes:</strong></p>
            <ul>
              {hook.commonMistakes.map((mistake, i) => <li key={i}>{mistake}</li>)}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReactHooks;
